#!/bin/bash
helm repo add cilium https://helm.cilium.io/ || true
helm repo update
helm install cilium cilium/cilium --version "v1.17.3"   --namespace kube-system --create-namespace   --set installCRDs=true   --set ipam.mode=cluster-pool   --set cluster.name=cluster1   --set cluster.id=1   --set k8sServiceHost=192.168.2.3   --set k8sServicePort=6443   --set operator.replicas=1   --set cleanCiliumState=true   --set cleanCiliumBpfState=true   --set ipam.operator.clusterPoolIPv6PodCIDRList="{2001:db8:1:10::/112}"   --set bgpControlPlane.enabled=true   --set ipv4.enabled=true   --set ipam.operator.clusterPoolIPv4PodCIDRList="{10.42.0.0/16}"   --set service.cidr=10.96.0.0/12   --set ipv4NativeRoutingCIDR=10.42.0.0/16   --set ipv4.enabled=false   --set enableIPv4Masquerade=false   --set ipv6.enabled=true   --set routingMode=native   --set ipv6NativeRoutingCIDR=2001:db8:1:10::/112   --set autoDirectNodeRoutes=true   --set loadBalancer.mode=ipip   --set bpf.masquerade=false
